	GF_LOOP: for(i=0; i<GF_ORDER; i++)
	{
		#pragma HLS pipeline
		DATA_LOOP: for(j=0; j<DATA; j++)
		{
			#pragma HLS pipeline
			ERROR_LOOP: for(k=0; k<ERRORS; k++)
			{
				unsigned char tmp = r[k][j];
				// unsigned char tmp = 24;
				// update c
				c[k] = c[k] ^  ( ((d[j]>>i)&1)? tmp : 0 );
				// update ram
				r[k][j] = ((tmp>>7)&1) ?  ((tmp<<1)^gf_poly) : (tmp<<1);
			}
		}
	}
